        {/* Set up the route for the second product */}
        <Link to={`/products/${secondProduct.id}`}>
          <Product data={secondProduct} />
        </Link>
        {/* Set up the route for the third product */}
        <Link to={`/products/${thirdProduct.id}`}>
          <Product data={thirdProduct} />
        </Link>
      </div>
      {/* ... (similarly for other product sections) */}

