import React from 'react';
import { withRouter } from 'react-router-dom';
const ArticlePage = ({ match }) => {
  const { id } = match.params; // Assuming you use the 'id' parameter from the URL to identify the article

  // Fetch the article content based on the 'id' from your data source or API
  // You can store this in a state or retrieve it on the fly depending on your setup

  // For demonstration purposes, let's assume the article content is stored in a variable called 'articleContent'
  const articleContent = `Article Content for article with ID: ${id}`;

  return (
    <div>
      <h1>Article {id}</h1>
      <p>{articleContent}</p>
    </div>
  );
};

export default ArticlePage;
