import React from 'react';
import { PRODUCTS } from '../../products';
import { Product } from './product';
import { Switch, Link } from 'react-router-dom';
import './shop.css';
import SwipeableTextMobileStepper from '../../components/carousel'

export const Shop = () => {
  const firstProduct = PRODUCTS[0];
  const secondProduct = PRODUCTS[1];
  const thirdProduct = PRODUCTS[2];
  const fourthProduct = PRODUCTS[3];
  const fifthProduct = PRODUCTS[4];
  const sixedProduct = PRODUCTS[5];
  const seventhProduct = PRODUCTS[6];
  const eightProduct = PRODUCTS[7];
  const ninthProduct = PRODUCTS[8];

  return (
    <div className='shop'>
      <button>Dickâ€™s Fapper</button>
      <div className='Carousel'>
        <SwipeableTextMobileStepper />
      </div>
      <div className='shopTitle'>
        <h1>Welcome Page</h1>
      </div>
      <div className='products'>
        {/* Set up the route for the first product */}
        <Link to={`/products/${firstProduct.id}`}>
          <Product data={firstProduct} />
        </Link>
        {/* Set up the route for the second product */}
        <Link to={`/products/${secondProduct.id}`}>
          <Product data={secondProduct} />
        </Link>
        {/* Set up the route for the third product */}
        <Link to={`/products/${thirdProduct.id}`}>
          <Product data={thirdProduct} />
        </Link>
        <Link to={`/products/${fourthProduct.id}`}>
          <Product data={thirdProduct} />
        </Link>
        <Link to={`/products/${fifthProduct.id}`}>
          <Product data={thirdProduct} />
        </Link>
        <Link to={`/products/${sixedProduct.id}`}>
          <Product data={thirdProduct} />
        </Link>
        <Link to={`/products/${seventhProduct.id}`}>
          <Product data={thirdProduct} />
        </Link>
        <Link to={`/products/${eightProduct.id}`}>
          <Product data={thirdProduct} />
        </Link>
        <Link to={`/products/${ninthProduct.id}`}>
          <Product data={thirdProduct} />
        </Link>
      </div>
      {/* <Switch>
        <Route path="/products/:id" component={ProductPage} />
      </Switch> */}
    </div>
  );
};
const ProductPage = ({ match }) => {
  const productId = match.params.id;
  // Fetch the product details using the productId and display them here
  return (
    <div>
      <h2>Product Details</h2>
      <p>Product ID: {productId}</p>
      {/* Display the rest of the product details */}
    </div>
  );
};
